/*! special helpers*/


(function() {
  var Handlebars, Utils, assemble, embed, gist, grunt, jsfiddle, path, _;

  Handlebars = require('../helpers/helpers').Handlebars;

  assemble = require('assemble');

  Utils = require('../utils/utils');

  grunt = require('grunt');

  path = require('path');

  _ = require('lodash');

  module.exports = {
    embed: embed = function(src, lang) {
      var content, dest, ext, gfmLang, output, result;
      dest = this.page.dest;
      ext = Utils.getExt(dest);
      content = Utils.globFiles(src);
      gfmLang = path.extname(src).replace(/^(\.)/gm, '');
      if (Utils.isUndefined(lang)) {
        lang = gfmLang;
      } else {
        lang = lang;
      }
      switch (gfmLang) {
        case "md":
        case "markdown":
        case "mdown":
          output = content.replace(/^(```)/gm, '&#x60;&#x60;&#x60;');
          lang = "markdown";
          break;
        case "txt":
          output = content;
          lang = "text";
          break;
        case "hbs":
        case "hbars":
          output = content.replace(/^(---)/gm, '---');
          lang = "handlebars";
          break;
        case "yml":
        case "yaml":
          output = content;
          lang = "yaml";
          break;
        case "less":
          output = content;
          lang = "scss";
          break;
        case void 0:
          output = content;
          lang = "";
          break;
        default:
          output = content;
          lang = "";
      }
      if (ext = '.html') {
        result = '<pre>\n' + lang + '\n' + output + '\n</pre>\n';
      } else {
        result = '\n```' + lang + '\n' + output + '\n```\n';
      }
      return Utils.safeString(result);
    },
    jsfiddle: jsfiddle = function(id, tabs) {
      var result;
      if (Utils.isUndefined(tabs)) {
        tabs = "result,js,html,css";
      }
      result = "<iframe width=\"100%\" height=\"300\" src=\"http://jsfiddle.net/" + id + "/embedded/" + tabs + "/presentation/\" allowfullscreen=\"allowfullscreen\" frameborder=\"0\"></iframe>";
      return Utils.safeString(result);
    },
    gist: gist = function(id, file) {
      var result;
      id = Handlebars.Utils.escapeExpression(id);
      if (Utils.isUndefined(file)) {
        file = "";
      }
      result = "<script src=\"https://gist.github.com/" + id + ".js\"></script>";
      return Utils.safeString(result);
    }
  };

  module.exports.register = function(Handlebars, options) {
    Handlebars.registerHelper("embed", embed);
    Handlebars.registerHelper("jsfiddle", jsfiddle);
    Handlebars.registerHelper("gist", gist);
    return this;
  };

}).call(this);
